/*Write a program to define a Rectangle class with the following:
Data Members:

o length and width (private)

Member Functions:

o setDimensions(int l, int w) to set the length and width.
o area() to calculate and return the area of the rectangle.
o perimeter() to calculate and return the perimeter.

Tasks:

o Create multiple Rectangle objects.
o Initialize their dimensions and display their area and perimeter.*/

#include<iostream>
using namespace std;

class rectangle
{
    int l,w;

public:

   void setdimensions()
   {

       cout<<"\n\nenter length and width :";
       cin>>l>>w;
   }
   int area()
   {

       return l*w;
   }
   int perimeter()
   {
       return 2*(l+w);
   }
};
int main()
{
    int n;

    cout<<endl<<"enter the number of rectangles u want to calculate data
for :";
    cin>>n;
    rectangle r[n];

    for(int i=0 ; i<n ; i++)
    {
         r[i].setdimensions();
          cout<<"\nAREA :"<<r[i].area();
    cout<<"\nPERIMETER :"<<r[i].perimeter();
    }


    return 0;
}

