#include <iostream>
#include <string>
using namespace std;

class FleetManager;


class Person {
public:
    string Name;
    Person() {}
    Person(string Name) : Name(Name) {}
    virtual void display() = 0;
};
class Driver : public Person {
private:
    int id;
    string License_num;

public:
    Driver() {}
    Driver(int id, string Name, string License_num) : Person(Name), id(id),
License_num(License_num) {}

    void display() override {
        cout << "Driver ID: " << id << " | Name: " << Name << " | License
Number: " << License_num << endl;
    }

    friend class FleetManager;
};


class Vehicle {
private:
    int Id;
    string type;
    string status;
    float fuel_level;

public:
    Vehicle() {}
    Vehicle(int Id, string type, string status, float fuel_level) : Id(Id),
type(type), status(status), fuel_level(fuel_level) {}

    void Display() {
        cout << "Vehicle ID: " << Id << " | Type: " << type << " | Status:
" << status << " | Fuel Level: " << fuel_level << endl;
    }

    friend class FleetManager;
};


class FleetManager {
private:
    Vehicle** fleet;
    Driver** drivers;
    int vehicleCount;
    int driverCount;
    int maxVehicles;
    int maxDrivers;

public:
    FleetManager(int maxV = 100, int maxD = 100) {
        maxVehicles = maxV;
        maxDrivers = maxD;
        vehicleCount = 0;
        driverCount = 0;
        fleet = new Vehicle*[maxVehicles];
        drivers = new Driver*[maxDrivers];
    }

    ~FleetManager() {
        for (int i = 0; i < vehicleCount; i++)
            delete fleet[i];
        delete[] fleet;

        for (int i = 0; i < driverCount; i++)
            delete drivers[i];
        delete[] drivers;
    }

    void Add_Vehicle() {
        if (vehicleCount >= maxVehicles) {
            cout << "Fleet is Full. Cannot add more Vehicles." << endl;
            return;
        }

        int id;
        string type, status;
        float fuel;
        cout << "Enter Vehicle ID: ";
        cin >> id;
        cout << "Enter Vehicle Type: ";
        cin >> type;
        cout << "Enter Vehicle Status (Available/Busy): ";
        cin >> status;
        cout << "Enter Fuel Level: ";
        cin >> fuel;

        fleet[vehicleCount++] = new Vehicle(id, type, status, fuel);
        cout << "Vehicle added successfully.\n";
    }

    void View_Vehicles() {
        if (vehicleCount == 0) {
            cout << "No vehicles found.\n";
            return;
        }

        for (int i = 0; i < vehicleCount; i++)
            fleet[i]->Display();
    }

    void Add_Driver() {
        if (driverCount >= maxDrivers) {
            cout << "Driver list is full. Cannot add more drivers.\n";
            return;
        }

        int id;
        string name, license;
        cout << "Enter Driver ID: ";
        cin >> id;
        cout << "Enter Driver Name: ";
        cin >> name;
        cout << "Enter License Number: ";
        cin >> license;

        drivers[driverCount++] = new Driver(id, name, license);
        cout << "Driver added successfully.\n";
    }

    void View_Drivers() {
        if (driverCount == 0) {
            cout << "No drivers found.\n";
            return;
        }

        for (int i = 0; i < driverCount; i++)
            drivers[i]->display();
    }
};


void Show_Menu() {
    cout << "\n--- Smart Transport Fleet Management System ---\n";
    cout << "1. Add Vehicle\n";
    cout << "2. View Vehicles\n";
    cout << "3. Add Driver\n";
    cout << "4. View Drivers\n";
    cout << "5. Exit\n";
    cout << "Enter your choice: ";
}

int main() {
    FleetManager manager;
    int choice;

    do {
        Show_Menu();
        cin >> choice;

        switch (choice) {
            case 1:
                manager.Add_Vehicle();
                break;
            case 2:
                manager.View_Vehicles();
                break;
            case 3:
                manager.Add_Driver();
                break;
            case 4:
                manager.View_Drivers();
                break;
            case 5:
                cout << "Exiting the system.\n";
                break;
            default:
                cout << "Invalid choice. Try again.\n";
        }

    } while (choice != 5);

    return 0;
}
